def step_status(num):
    numbers = {
        0: "Building detection and segmentation",
        1: "Initial 2D building polygon extraction",
        2: "Building rectangle decomposition",
        3: "Building rectangle orientation refinement",
        4: "3D model fitting",
        5: "Mesh generation",
        6: "Model complete"
    }
    return numbers.get(num, None)

